import React, {useContext,useState} from 'react'
import {AlbumContext} from '../App'
import './Album.css'
import PlayCircleFilledWhiteIcon from '@material-ui/icons/PlayCircleFilledWhite';
import {BrowserRouter as Router, Switch, Route, Link} from 'react-router-dom';
import AlbumSongsList from './AlbumSongsList'
import MoreVertIcon from '@material-ui/icons/MoreVert';


function Album() {
    const albums=useContext(AlbumContext);

    const Content=   <div className='Albumlist'>
                            {albums.map(album=>(
                            <Router>
                            <Link to={`/AlbumSongsList/${album.id}`}>
                            <div className='AlbumCard' onClick={() =>{<Route path="/AlbumSongsList/:name" exact component={AlbumSongsList}/>}}>
                            <img src='../images/unkownAlbum.png' className='AlbumImg' alt='unknownAlbum'/> 
                            <PlayCircleFilledWhiteIcon className='PlayCircle'/>
                            <MoreVertIcon className='MoreVertIcon'/>
                             <h4 className='AlbumTitle'>{album.title.toUpperCase()}</h4>  
                              </div>
                              </Link>
                              </Router>
                              ))}  
                              </div> 



    return (
        <>    
        <p className='ActiveTitle'>All Songs</p>   
        {Content}
        </>
    )
}

export default Album
