import React, {useContext,useEffect,useState} from 'react'
import {AlbumContext,SongsContext} from '../App'
import AddIcon from '@material-ui/icons/Add';
import './AllSongs.css'
import CurrentPlaying from './CurrentPlaying';
let faker = require('faker');


export const CurrentSongTitle=React.createContext()
export const CurrentSongTime=React.createContext()

const randomTimeGenerator=()=>{
    const randomNumber=Math.random()*10;
    return randomNumber.toFixed(2)
}

function AllSongs() {

    const albums=useContext(AlbumContext);
    const songs=useContext(SongsContext);
    var randomName = faker.name.findName(); 
    var TitleTrack='';
    var Duration='';
    //this is to provide current playing song to currentplaying component
    const [currentTitle, setcurrentTitle]= useState('');
    const [currentTime, setcurrentTime]= useState('');




    const eventSongHandler=(e) =>{
        const domelements = e.target
        console.log(domelements);
         TitleTrack= domelements.querySelectorAll(".TrackTitle")[0].innerHTML;
         Duration= domelements.querySelectorAll(".DurationofSong")[0].innerHTML;    
         console.log(TitleTrack)
      }


      useEffect(() => {
        console.log('hiii');
        console.log(Duration);
        setcurrentTitle(TitleTrack);
        setcurrentTime(Duration);
        console.log(currentTitle)
      }, [TitleTrack,Duration]);








    return (
        <>

        <table className='SongsWrapper'>

        <tr className='TitleHeading'>
            <th className='Songimg'>Image</th>
            <th className='TrackTitleHeading'>Title</th>
            <th className='DurationHeading'>Time</th>
            <th className='SingerHeading'>Artist</th>
            <th className='AlbumHeading'>Album</th>
            <th className='AddtoPlaylist'>Playlist</th>
        </tr>

        {songs && songs.map(song=>(

                     <tr className='EachSongContainer' onClick={eventSongHandler}>
                         <td className='ImgContainer'> <img src={song.thumbnailUrl} className='Img-thumbnail' alt='thumbnail'/> </td>
                         <td className='TrackTitle' >{song.title.charAt(0).toUpperCase() + song.title.slice(1)} <span class="tooltiptext">{song.title.charAt(0).toUpperCase() + song.title.slice(1)}</span></td>
                        <td className='DurationofSong'>{randomTimeGenerator()}</td>  
                        <td className='Singer'>{faker.name.findName()}</td>   
                        <td className='AlbumName'>{ albums && albums.map(album =>(
                            (album.id===song.albumId) && album.title 
                       )) }</td>   
                       <td className='AddtoFav'> <AddIcon /></td>         
                     </tr>    
))} 
        </table>
        </>
    )
}

export default AllSongs
