import React, {useContext,useEffect,useState} from 'react'
import {AlbumContext,SongsContext} from '../App'
import AddIcon from '@material-ui/icons/Add';
import './AllSongs.css'
var faker = require('faker');

const randomTimeGenerator=()=>{
    const randomNumber=Math.random()*10;
    return randomNumber.toFixed(2)
}

function AllSongs() {
    const [currentSong,setCurrentSong]=useState();
    const albums=useContext(AlbumContext);
    const songs=useContext(SongsContext);
    var randomName = faker.name.findName(); 



    return (
        <div className='SongsWrapper'>

        <div className='TitleHeading'>
        <p>Song Image</p> 
        <p className='TrackTitleHeading'>Track</p>
         <p className='SingerHeading'>Singer</p>
        <p className='DurationHeading'>Duration</p>
        <p> Add to Playlist</p>
     </div>

        {songs && songs.map(song=>(

            <div className='EachSongContainer'>
            <img src={song.thumbnailUrl} className='Img-thumbnail' alt='thumbnail'/> 
            <p className='TrackTitle'>{song.title.charAt(0).toUpperCase() + song.title.slice(1)}</p>
             <p className='Singer'>{faker.name.findName()}</p>

            <p className='DurationofSong'>{randomTimeGenerator()}</p>

                <AddIcon className='AddtoFav'/>

         </div>


))} 


        </div>
    )
}

export default AllSongs
