function Playlists() {

    const dataPlaylist=[
      {
        id:1,
        name:"Carnatic",
        playlist:[]
      },
      {
        id:2,
        name:"Metal-Rock",
        playlist:[]
      }
    ]

    const [Playlists, setPlaylists] = useState([])
    const [isModalOpen,setisModalOpen]=useState(false);




