import React, {useContext,useState} from 'react'
import {AlbumContext} from '../App'
import './Album.css'


function Album() {
    const [readMore,setReadMore]=useState(false);
    const albums=useContext(AlbumContext);

    const linkName=readMore?'Show Less':'Show More'

    const extraContent=   <div className='Albumlist'>
                            {albums.map(album=>(
                            <div className='AlbumCard'>
                            <img src='unkownAlbum.png' className='Img' alt='unknownAlbum'/> 
                             <h4 className='AlbumTitle'>{album.title.toUpperCase()}</h4>  
                              </div>
                              ))}  
                              </div> 



    return (
        <>
        <h1>ALBUMS</h1>
        <a className="read-more-link" onClick={()=>{setReadMore(!readMore)}}><h2>{linkName}</h2></a>
        {readMore && extraContent}
        </>
    )
}

export default Album
