import React, {useContext,useEffect} from 'react'
import {AlbumContext,SongsContext} from '../App'
import AddIcon from '@material-ui/icons/Add';
import './AllSongs.css'



function AllSongs() {
    const albums=useContext(AlbumContext);
    const songs=useContext(SongsContext);
    const AlbumsAsArray = Object.entries(albums);

    const albumObj={};



    return (
        <>

        {songs && songs.map(song=>(

            <div className='EachSongContainer'>
            { albumObj = Object.fromEntries(AlbumsAsArray.filter(([key, value]) => value === song.albumId))}
            <div className='AllSongImgContainer'>
            <img src={song.thumbnailUrl} className='Img-thumbnail' alt='thumbnail'/> 
            <p className='TrackTitle'>{song.title}</p>
            </div>
            <div className='ArtistNameContainer'>
             <p className='singer'>Hariharan</p>
             </div>
            <div className='DurationContainer'>
            <p className='DurationofSong'>4:23</p>
            </div>
            <div className='AddToFav'>
                <AddIcon/>
            </div>
         </div>


))} 


        </>
    )
}

export default AllSongs
