import React, {useState, useEffect} from 'react'
import axios from 'axios'
import './App.css';
import Music from './components/Music'
import Searchbar from './components/Searchbar'
import Types from './components/Types';
import Header from './components/Header';
import Album from './components/Album';
import Sidebar from './components/Sidebar';
import MainBody from './components/MainBody';
import CurrentPlaying from './components/CurrentPlaying'
import AllSongs from './components/AllSongs';
import {BrowserRouter, Switch,Route} from 'react-router-dom'



export const AlbumContext=React.createContext()
export const SongsContext=React.createContext()

function App() {

  const [albums, setAlbums]= useState([])
  const [songs, setSongs]= useState([])

  const setCurrentTitle=(title)=>{
    setState({...state,CurrentTitle:title})
  }

  const setCurrentTime=(time)=>{
    setState({...state,CurrentTime:time})
  }

  const setCuttentImg=(img)=>{
    setState({...state,CurrentImg:img})
  }

  const initState = {
    CurrentTitle:'',
    setCurrentTitle:setCurrentTitle,
    CurrentTime:'',
    setCurrentTime:setCurrentTime,
    CurrentImg:'' ,
     setCuttentImg:setCuttentImg
  }

  const [state,setState] = useState(initState)





  useEffect(() => {
    axios.get('https://jsonplaceholder.typicode.com/albums')
    .then(response=> setAlbums(response.data))
    .catch(error=> console.log('Incorrect URL'))
  },[]);

  useEffect(() => {
      axios.get('https://jsonplaceholder.typicode.com/photos')
      .then(response=> {setSongs(response.data)})
      .catch(error=> console.log('Incorrect URL'))
    },[]);




  return (
    <div className="App">
    <BrowserRouter>

    <Sidebar/>
    <SongsContext.Provider value={songs}>
    <AlbumContext.Provider value={albums}>
    <MainBody/>
    </AlbumContext.Provider>
    </SongsContext.Provider>

    <CurrentPlaying/>


    </BrowserRouter>

    </div>
  );
}

export default App;
