import React, {useContext,useState} from 'react'
import {AlbumContext} from '../App'
import './Album.css'
import PlayCircleFilledWhiteIcon from '@material-ui/icons/PlayCircleFilledWhite';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import AlbumSongsList from './AlbumSongsList'


function Album() {
    const [readMore,setReadMore]=useState(false);
    const albums=useContext(AlbumContext);

    const linkName=readMore?'':'Show More'

    const extraContent=   <div className='Albumlist'>
                            {albums.map(album=>(
                            <Router>
                            <div className='AlbumCard' onClick={() =>{<Route path="/AlbumSongsList" component={AlbumSongsList}/>}}>
                            <img src='unkownAlbum.png' className='Img' alt='unknownAlbum'/> 
                            <PlayCircleFilledWhiteIcon className='PlayCircle'/>
                             <h4 className='AlbumTitle'>{album.title.toUpperCase()}</h4>  
                              </div>
                              </Router>
                              ))}  
                              </div> 



    return (
        <>
        <h1>ALBUMS</h1>
        <a className="read-more-link" onClick={()=>{setReadMore(!readMore)}}><h2>{linkName}</h2></a>
        {readMore && extraContent}
        </>
    )
}

export default Album
